import logo from './logo.svg';
import './App.css';
import Graph from "react-graph-vis";
import React, { useState } from "react";

const options = {
  layout: {
    hierarchical: false
  },
  edges: {
    color: "#000000"
  }
};

function randomColor() {
  const red = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const green = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const blue = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  return `#${red}${green}${blue}`;
}

const App = () => {
  const createNode = (x, y, selected) => {
    state = setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter + 1;
      const from = parseInt(selected)
      return {
        graph: {
          nodes: [
            ...nodes,
            { id: id, label: `Node ${id}`, color, x, y }
          ],
          edges: [
            ...edges,
            { from: from, to: id },
            { from: id, to: from }
          ]
        },
        counter: id,
        ...rest
      }
    });
  }

  const createEdge = (selected, selectedAux) => {
    state = setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter;
      const from = parseInt(selectedAux)
      const to = parseInt(selected)
      selected = null
      for(let i of edges) {
        if(i.from === from && i.to === to) {
          //console.log(`Ya existe ${i}`)
          return {
            graph: {
              nodes: [
                ...nodes,
              ],
              edges: [
                ...edges,
              ]
            },
            counter: id,
            ...rest
          }
        }
      }
      return {
        graph: {
          nodes: [
            ...nodes,
          ],
          edges: [
            ...edges,
            {from: from, to: to}
          ]
        },
        counter: id,
        ...rest
      }
    });
  }

  const infoEdges = (selec) => {
    state = setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter;
      const from = parseInt(selectedAux)
      const to = parseInt(selected)
      for(let i of edges) {
        if(i.id === selec.toString()) {
          console.log("Selected edges:");
          console.log(i)
        }
      }
      return {
        graph: {
          nodes: [
            ...nodes,
          ],
          edges: [
            ...edges,
          ]
        },
        counter: id,
        ...rest
      }
    });
  }

  const infoNodes = (selec) => {
    state = setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter;
      const from = parseInt(selectedAux)
      const to = parseInt(selected)
      for(let n of nodes) {
        if(n.id === parseInt(selec)) {
          console.log("Selected node:");
          console.log(n)
          console.log("Edges:");
          for(let e of edges) {
            if(e.from === n.id || e.to === n.id) {
              console.log(e)
            }
          }
        }
      }
      return {
        graph: {
          nodes: [
            ...nodes,
          ],
          edges: [
            ...edges,
          ]
        },
        counter: id,
        ...rest
      }
    });
  }

  var selected
  var selectedAux
  var selectedNode
  var selectedEdge
  var color = randomColor()

  const stateinfo = () => {
    console.log(state)
  } 

  var [state, setState] = useState({
    counter: 1,
    graph: {
      nodes: [
        { id: 1, label: `Node ${1}`, color }
      ],
      edges: [
      ],
    },
    events: {
      selectNode: ({ nodes }) => {
        //console.log("Selected nodes:");
        //console.log(nodes);
        //alert("Selected node: " + nodes);
        selectedNode = nodes
        selected = nodes
        infoNodes(nodes)
        createEdge(selectedNode, selectedAux);
      },
      selectEdge: ({ edges }) => {
        infoEdges(edges)
        stateinfo()
      },
      doubleClick: ({ pointer: { canvas } }) => {
        createNode(canvas.x, canvas.y, selectedNode);
      },
      hoverEdge: ({ pointer: { canvas } }) => {
        //createEdge(canvas.x, canvas.y, selectedEdge);
      },
      deselectNode:  () => {
        //console.log("Deselected node:");
        //console.log(selected);
        selectedAux = selected
      },
      deselectEdge:  () => {
        //console.log("Deselected edge:");
        //console.log(selected);
        selectedAux = selected
      }
    }
  })
  const { graph, events } = state;
  return (
    <div>
      <div className="App">
      <header className="App-header">
      <Graph graph={graph} options={options} events={events} style={{ height: "100vh" }} />
      </header>
      </div>
    </div>
  );
}

export default App;
