import logo from './logo.svg';
import './App.css';
import Graph from "react-graph-vis";
import React, { useState } from "react";

const options = {
  layout: {
    hierarchical: false
  },
  edges: {
    color: "#000000"
  }
};

function randomColor() {
  const red = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const green = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const blue = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  return `#${red}${green}${blue}`;
}

const App = () => {
  const createNode = (x, y, selected) => {
    const color = randomColor();
    setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter + 1;
      var from = Math.floor(Math.random() * (counter - 1)) + 1;
      from = parseInt(selected)
      return {
        graph: {
          nodes: [
            ...nodes,
            { id: id, label: `Node ${id}`, color, x, y }
          ],
          edges: [
            ...edges,
            { from, to: id }
          ]
        },
        counter: id,
        ...rest
      }
    });
  }
  var nodesArr = []
  var edgesArr = []
  const createEdge = (selected, selectedAux) => {
    const color = randomColor();
    setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter;
      const from = parseInt(selectedAux)
      const to = parseInt(selected)
      console.log(Object.values(edges))
      if(!edgesArr.includes(edges))
        edgesArr.push(edges)
      console.log(edgesArr)
      return {
        graph: {
          nodes: [
            ...nodes,
          ],
          edges: [
            ...edges,
            {from: from, to: to}
          ]
        },
        counter: id,
        ...rest
      }
    });
  }
  var selected
  var selectedAux
  var selectedNode
  var selectedEdge

  const [state, setState] = useState({
    counter: 1,
    graph: {
      nodes: [
        { id: 1, label: `Node ${1}`, randomColor }
      ],
      edges: [
      ]
    },
    events: {
      selectNode: ({ nodes, edges}) => {
        console.log("Selected nodes:");
        console.log(nodes);
        //alert("Selected node: " + nodes);
        selectedNode = nodes
        selected = nodes
        if (!nodesArr.includes(selected))
        nodesArr.push(selected)
        //if (selected !== selectedAux && parseInt(selected) !== parseInt(selectedAux))
        createEdge(selectedNode, selectedAux);
      },
      selectEdge: ({ edges }) => {
        //console.log("Selected edges:");
        console.log(edges);
        //selected = edges
        //selectedEdge = edges
        /*if (!(edgesArr.includes(edges)))
        edgesArr.push(edges)*/
      },
      doubleClick: ({ pointer: { canvas } }) => {
        if(nodesArr.includes(selected))
        createNode(canvas.x, canvas.y, selectedNode, selectedAux);
        //if(edgesArr.includes(selected))
        //createEdge(selectedNode, selectedAux);
      },
      hoverEdge: ({ pointer: { canvas } }) => {
        //createEdge(canvas.x, canvas.y, selectedEdge);
      },
      deselectNode:  () => {
        console.log("Deselected node:");
        console.log(selected);
        selectedAux = selected
      },
      deselectEdge:  () => {
        console.log("Deselected edge:");
        console.log(selected);
        selectedAux = selected
      }
    }
  })
  const { graph, events } = state;
  return (
    <div>
      <div className="App">
      <header className="App-header">
      <Graph graph={graph} options={options} events={events} style={{ height: "100vh" }} />
      </header>
      </div>
    </div>
  );
}

export default App;
