import logo from './logo.svg';
import './App.css';
import Graph from "react-graph-vis";
import React, { useState } from "react";

const options = {
  layout: {
    hierarchical: false
  },
  edges: {
    color: "#000000"
  }
};

function randomColor() {
  const red = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const green = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const blue = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  return `#${red}${green}${blue}`;
}

const App = () => {
  const createNode = (x, y, selected) => {
    const color = randomColor();
    setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter + 1;
      var from = Math.floor(Math.random() * (counter - 1)) + 1;
      from = parseInt(selected)
      return {
        graph: {
          nodes: [
            ...nodes,
            { id: id, label: `Node ${id}`, color, x, y }
          ],
          edges: [
            ...edges,
            { from, to: id }
          ]
        },
        counter: id,
        ...rest
      }
    });
  }
  const createEdge = (x, y, selected) => {
    const color = randomColor();
    setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter + 1;
      const from = parseInt(selected)
      return {
        graph: {
          nodes: [
            ...nodes,
          ],
          edges: [
            ...edges,
            { from, to: id }
          ]
        },
        counter: id,
        ...rest
      }
    });
  }
  var selectedNode
  var selectedEdge
  const [state, setState] = useState({
    counter: 1,
    graph: {
      nodes: [
        { id: 1, label: `Node ${1}`, randomColor },
      ],
      edges: [

      ]
    },
    events: {
      select: ({ nodes, edges }) => {
        console.log("Selected nodes:");
        console.log(nodes);
        console.log("Selected edges:");
        console.log(edges);
        //alert("Selected node: " + nodes);
        selectedNode = nodes
        selectedEdge = edges
      },
      doubleClick: ({ pointer: { canvas } }) => {
        if(nodes.includes(selectedNode))
        createNode(canvas.x, canvas.y, selectedNode);
        if(edges.includes(selectedEdge))
        createEdge(canvas.x, canvas.y, selectedEdge);
      }
    }
  })
  const { graph, events } = state;
  return (
    <div>
      <div className="App">
      <header className="App-header">
      <Graph graph={graph} options={options} events={events} style={{ height: "100vh" }} />
      </header>
      </div>
    </div>
  );
}

export default App;
